gameforge.locale.questscroll5.mission_16_mis = "Destruye 3 Metin de Oscuridad. "
gameforge.locale.questscroll5.mission_16_text = "Las Piedras de Metin estan cayendo muy cerca[ENTER]de nuestra ciudad principal, el chi de estas[ENTER]Piedras han despertado la rabia a los montruos.[ENTER]Debemos terminar con estas Piedras antes de que[ENTER]sea demasiado tarde. "
