gameforge.locale.questscroll5.mission_20_mis = "Mata 30 Soldados Mono y 30 Lanzadores Mono. "
gameforge.locale.questscroll5.mission_20_text = "El sitio de los Monos Normales es la Mazmorra de Monos del Desierto. Estos monos generalmente no son peligrosos. Pero cuando se pudren de rabia, pueden llegar a serlo. Debes acabar con ellos. "
gameforge.locale.questscroll5.mission_21_hunt = "Mazmorra Fácil De Monos 1 "
gameforge.locale.questscroll5.mission_21_mis = "Mata 30 Soldados Mono Debil y 30 Lanzadores Mono Debil. "
gameforge.locale.questscroll5.mission_21_text = "El sitio de los Monos Fáciles es la Mazmorra de Monos de la segunda aldea. Estos monos generalmente no son peligrosos. Pero cuando se pudren de rabia, pueden llegar a serlo. Debes acabar con ellos, antes de que sea tarde. "
gameforge.locale.questscroll5.mission_22_hunt = "Siembran viento y recogen tempestades "
gameforge.locale.questscroll5.mission_22_mis = "Mata a 4 Chuongs del Clan Viento Negro. "
gameforge.locale.questscroll5.mission_22_text = "Los Chuonds, Lideres del Clan Viento Negro, Han comenzado a contraatacar. Como maestros de la esgrima han desafiado a nuestros mejores guerreros. Eres nuestra esperanza! Acaba con ellos. "
