gameforge.main_quest_lv66._280_say = "Si, yo te ayudaré. "
gameforge.main_quest_lv66._290_say = "Oh, acabas de darnos luz y  esperanza![ENTER]Ahora que sé que estás de nuestro lado, voy[ENTER]a decirte toda la verdad:[ENTER]Yo, Lee Chung, soy el comandante del guerrero de la sombra.[ENTER]Somos una unidad secreta de élite del[ENTER]ejército de Lee Seops. "
gameforge.main_quest_lv66._300_sayTitle = "Lee Chung "
