gameforge.main_quest_lv84._60_sendLetter = "General Tártaros "
gameforge.main_quest_lv84._70_say = "Uiliza la lágrima celestial únicamente cuando[ENTER]hayas alcanzado el quinto nivel de las[ENTER]Catacumbas del Diablo. La entrada[ENTER]a las Catacumbas del Diablo se encuentra[ENTER]en la parte posterior de la torre demoniaca. "
gameforge.main_quest_lv84._80_say = "Las poderes sagrados comienzan a actuar[ENTER]en la lágrima celestial.Sientez[ENTER]una tremenda energia que fluye a través[ENTER]de tu cuerpo. El poder de esta piedra[ENTER]es fascinante! Destruye al general Tártaro. "
