gameforge.main_quest_lv92._410_say = "Llevale la carta cifrada a Yon-I-Walker. "
gameforge.main_quest_lv92._420_say = "Hola! Que puedo hacer por ti?"
gameforge.main_quest_lv92._430_say = "Aqui está la carta de los ladrones rojos.[ENTER]Pero es como las anteriores y esta encriptada. "
gameforge.main_quest_lv92._440_say = "Déjame echar un vistazo.[ENTER]Hm, estos son los los Tritones.[ENTER]No debe ser dificil de descifrarla. "
gameforge.main_quest_lv92._450_say = "Cuánto tiempo necesitas? "
gameforge.main_quest_lv92._460_say = "No tan rápido.[ENTER]El cifrado no es particularmente complicado.[ENTER]Pero sin más ayuda, no podré descifrarla. "
gameforge.main_quest_lv92._470_say = "Claro que no. Eso hubiera sido demasiado fácil. "
gameforge.main_quest_lv92._480_say = "Veo que no has perdido tu sentido del humor, jeje.[ENTER]Para descifrar la carta, necesito la Escama de Oin.[ENTER]Puedes encontrarlo en los Generales Kappa, y[ENTER]los Tritones. Necesitaré 10 unidades. "
gameforge.main_quest_lv92._490_say = "Ahora mismo voy hacia allá. "
gameforge.main_quest_lv92._500_sayTitle = "Encuentra las Escamas de Oin! "
gameforge.main_quest_lv92._510_say = "Sólo al Tritón, rey de los tritones.[ENTER]Y al general Kappa. "
