gameforge.subquest_45._210_say = "Dann werde ich wohl einen stärkeren Helden fragen[ENTER]müssen. Leb wohl. "
gameforge.subquest_45._220_say = "Danke.[ENTER]Ich hoffe du hast diesmal mehr Erfolg! "
